import React, {useState, useEffect} from "react";
import Nav from './Nav'
import axios from 'axios';
import { Link } from "react-router-dom";


const App = () => {
	const [posts, setPosts] = useState([])

	const fetchPosts = () => {
		axios.get(`${process.env.REACT_APP_API}/posts`)
		.then(response => {
			// console.log(response)
			setPosts(response.data)
		})
		.catch(error => alert('Error fetching posts'));
	};

	useEffect(() => {
		fetchPosts()
	},[])

	return (
		<div className="container pb-5">
			<Nav />
			<br />
			<h1>Bee Well <span>üêù</span></h1>
			<hr/>
			{
				posts.map((post, i) => (
					<div className = "row" key={post._id} style={{borderBottom: '1px solid silver'}}>
						<div className="col pt-3 pb-2">
						<div className="row">
							<div className="col-md-10">
							<Link to={`/post/${post.slug}`}><h2>{post.title}</h2></Link>
						<p className="lead">{post.content.substring(0, 100)}</p>
						<p>Author <span className="badge">{post.user}</span> Published on{''} <span className="badge">{new Date(post.createdAt).toLocaleDateString()}</span></p>
							</div>
							<div className="col-md-2">
							<Link to={`/post/update/${post.slug}`}><a className="btn btn-sm btn-outline-warning">Update</a></Link>
							<button className="btn btn-sm btn-outline-danger ml-1">Delete</button>
							</div>
						</div>
						</div>
					</div>
				))
			}
		</div>
	);
};

export default App;
