import React, {useState} from 'react';
import axios from 'axios';
import Nav from './Nav'

const Create = () => {
    //state
    const [state, setState] = useState({
        title: '',
        content: '',
        user: ''
    })
    
    //destructure values from state
    const {title, content, user} = state

    //onChange event handler
    const handleChange = name => event => {
        console.log("name", name, "event", event);
        setState({...state, [name]: event.target.value});
    };

    const handleSubmit = event => {
        event.preventDefault();
        axios
        .post(`${process.env.REACT_APP_API}/post`, { title, content, user })
        .then(response => {
            console.log(response);

            setState({...state, title: '', content: '', user: ''});

            alert(`Post titled ${response.data.title} is created`);
        })
        .catch(error => {
            console.log(error.response);
            alert(error.response.data.error);
        });
        
    };


   return (
    <div className="container pb-5">
        <Nav />
    <h1>Bee Post ğŸ</h1>
    <br/>
    <form onSubmit={handleSubmit}>
        <div className="form-group">
            <label className="text-muted">Title</label>
            <input onChange={handleChange('title')} value={title} type="text" className="form-control" placeholder="Post title" required/>
        </div>
        <div className="form-group">
            <label className="text-muted">Content</label>
            <textarea onChange={handleChange('content')} value={content} type="text" className="form-control" placeholder="Create a buzz..." required/>
        </div>
        <div className="form-group">
            <label className="text-muted">User</label>
            <input onChange={handleChange('user')} value={user} type="text" className="form-control" placeholder="Your name" required/>
        </div>
        <div>
            <button className="btn btn-primary">Create</button>
        </div>
    </form>
</div>
   )
};

export default Create;
